function forceZGeekEditor() {
    var editorIframe = $(".sceditor-container iframe")[0];
    if (editorIframe) {
        var doc = editorIframe.contentDocument || editorIframe.contentWindow.document;
        if (doc && !doc.getElementById('zgeek-fix')) {
            var style = doc.createElement('style');
            style.id = 'zgeek-fix';
            style.innerHTML = `
                body { 
                    background: #0d0d0d !important; 
                    color: #eeeeee !important; 
                    font-family: 'Segoe UI', Tahoma, sans-serif !important; 
                    font-size: 15px !important; 
                    line-height: 1.6 !important;
                }
                blockquote { 
                    background: #151515 !important; 
                    border: none !important;
                    border-left: 3px solid #00adef !important; 
                    color: #999 !important; 
                    padding: 10px !important;
                    margin: 10px 0 !important;
                }
                cite { color: #00adef !important; font-weight: bold !important; display: block !important; margin-bottom: 5px !important; }
            `;
            doc.head.appendChild(style);
        }
    }
}
// تحديث كل نصف ثانية للتأكد من بقاء الستايل
setInterval(forceZGeekEditor, 500);

